import { CloseButton, Heading, Input } from "@chakra-ui/react";
import { Message } from "./Message";

export const Chat = ({ messages, chatRoom, closeChat }) => {
  const [message, setMessage] = useState("");
  return (
    <div className="w-1/2 bg-white p-8 rounded shadow-lg">
      <div className="flex flex-row justify-between mb-5">
        <Heading size="lg">{chatRoom}</Heading>
        <CloseButton onClick={closeChat} />
      </div>
      <div className="flex flex-col overflow-auto scrool-smooth h-96 gap-3 pb-3">
        {messages.map((messageInfo, index) => (
          <Message messageInfo={messageInfo} key={index} />
        ))}
      </div>
      <div className="flex gap-3">
        <Input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
      </div>
    </div>
  );
};
